INSERT INTO  ila_action(ID, CREATED_DATE, LAST_MODIFIED_DATE, BRIEF, CUSTOMER_PARTICIPANTS, DETAIL, MILESTONE, OCCUR_DATE, SELF_PARTICIPANTS, ACTION_TYPE_ID, LEADS_ID, USER_ID) VALUES ('8a079cef3d21ac18013d244e7fd10005', '2013-03-01 12:55:57', '2013-03-01 12:55:57', '业务沟通', '刘来平', '1、工作汇报
（1）当前互联网清算项目情况汇报（退款业务）
（2）2013年互联网清算项目启动及规划，我们就清算工作，表达了公司参与的愿望，并且介绍这个工作将由杨兆昱参与。
2、刘来平就互联清算工作谈了一下几点：
1) 未来的清算平台不仅是为互联网服务，还会接入更多的服务产品的清算工作和分公司的收单清算等；因此是一个平台化的，标准化的，通用的、可扩展的系统；
2) 未来的清算系统不能仅站在财务部门的角度，而是从银联的业务和流程的角度来建设；
3) 希望我们作为开发商能够动用我们的力量和资源，一起为银联的创新业务做出一个具有创新和特色的清算平台', 0, '2013-01-10 00:00:00', '王浩\李颖山\楊兆昱\王老师', 'visit', '8a079cef3d21ac18013d244b51ed0003', '8a53b7823cf1edfd013cf1eface40000');
INSERT INTO  ila_action(ID, CREATED_DATE, LAST_MODIFIED_DATE, BRIEF, CUSTOMER_PARTICIPANTS, DETAIL, MILESTONE, OCCUR_DATE, SELF_PARTICIPANTS, ACTION_TYPE_ID, LEADS_ID, USER_ID) VALUES ('8a079cef3d21ac18013d2452537c0006', '2013-03-01 13:00:07', '2013-03-01 13:00:07', '互联网清算业务交流', '杨总/刘来平', '*财务支撑工作（核算准确、资金安全、业会合拢）业务<->会计<->资金
1、杨总：建立互联网清算平台（商户+持卡人），偏重于交易处理（标准化、规范化、自动化），定位于业务系生产统范畴，并兼顾满足管理需求。3月出需求，一个月审批周期，年底上线运行（需要新致公司参与需求调研，并提出建议）；建议成立跨部门团队；持卡人管理平台6月份上线
2、杨总：主要业包括银行（cups）、商户（比较分散，需要集中）、持卡人（空白）,银联峰会：重心从银行转到持卡人和商户
3、刘：系统涉及多用户（注重用户体验），调研内容关注分公司业务量，满足业务部门要求，ERP接口自动化；外部系统数据的不落地处理，个性化短信提示等', 0, '2013-01-25 00:00:00', '王浩\李颖山\楊兆昱\王老师', 'visit', '8a079cef3d21ac18013d244b51ed0003', '8a53b7823cf1edfd013cf1eface40000');
