INSERT INTO  ila_action(ID, CREATED_DATE, LAST_MODIFIED_DATE, BRIEF, CUSTOMER_PARTICIPANTS, DETAIL, MILESTONE, OCCUR_DATE, SELF_PARTICIPANTS, ACTION_TYPE_ID, LEADS_ID, USER_ID) VALUES ('8a079cef3d452a4d013d4ccdffb70002', '2013-03-09 09:40:01', '2013-03-09 09:40:01', '项目商务谈判', ' 刘来平/赵魁', '由于客户预算只有20万，我们的工作量估算大约为20多个人月，经过沟通，将部分功能进行裁减，工作量控制在10个人月的范围内。下周一再提供一份工作量评估数据，待确认后即可启动后续合同签订工作。', 1, '2013-03-08 00:00:00', '王浩/杨兆煜', 'visit', '8a079cef3d21ac18013d451edbb20012', '8a53b7823cf1edfd013cf1eface40000');
INSERT INTO  ila_action(ID, CREATED_DATE, LAST_MODIFIED_DATE, BRIEF, CUSTOMER_PARTICIPANTS, DETAIL, MILESTONE, OCCUR_DATE, SELF_PARTICIPANTS, ACTION_TYPE_ID, LEADS_ID, USER_ID) VALUES ('8a079cef3d598664013d5d6cb66b0001', '2013-03-12 15:07:18', '2013-03-12 15:07:18', '合同金额谈判', '刘来平', '针对我们提出的工作量估算和报价(24万),客户提出两个方案:
(1)范围不变,金额降至20万
(2)金额不变,增加部分需求
经过与项目组沟通,我们计划采用方案2,增加了小块内容,并已提交客户.接下来客户这里还需要将此情况报至公司领导正式审核,待通过后将会启动合同签订工作.', 1, '2013-03-12 00:00:00', '王浩', 'visit', '8a079cef3d21ac18013d451edbb20012', '8a53b7823cf1edfd013cf1eface40000');
INSERT INTO  ila_action(ID, CREATED_DATE, LAST_MODIFIED_DATE, BRIEF, CUSTOMER_PARTICIPANTS, DETAIL, MILESTONE, OCCUR_DATE, SELF_PARTICIPANTS, ACTION_TYPE_ID, LEADS_ID, USER_ID) VALUES ('8a079cef3d598664013d61f94bf00002', '2013-03-13 12:19:20', '2013-03-13 12:19:20', '框架项目范围讨论', '内审(姚俊)\信息系统部(周蕾)', '我方:
(1)2013年的框架项目规模基本上和12年持平;
(2)对于部分内容无法放进框架,建议另签项目,比如:内审部定额系统\电商营收稽核扩容\营改增改造\档案接口
客户:
(1)新领导更换后,对框架合同方式存在疑义,主要关注工作量是否饱和,后续可能会在核定方面加强监管
(2)由于营改增项目是2013年重点,之前的框架项目部分预算会有压缩
(3)对于内审定额的需求,建议先提供建设方案,待汇报后再确定如何处理
(4)对于电商营收稽核扩容部分需求,建议由财务部申请另建项目
(5)对于档案接口和营改增应该会走项目(资本性支出)
(6)对于维护项目中人员的投入和使用情况需要进行评估,并提出需要包含一些开发工作,并进行明确.
总体上感觉2013年需求还是可以的,但商务工作仍然不容乐观,需要投入较多的精力.', 1, '2013-03-13 00:00:00', '王浩\黄飞', 'visit', '40288c5e3cf7471f013cfaa95bd10002', '8a53b7823cf1edfd013cf1eface40000');
