INSERT INTO  ila_action(ID, CREATED_DATE, LAST_MODIFIED_DATE, BRIEF, CUSTOMER_PARTICIPANTS, DETAIL, MILESTONE, OCCUR_DATE, SELF_PARTICIPANTS, ACTION_TYPE_ID, LEADS_ID, USER_ID) VALUES ('8a079cef3d598664013dc3ffabd20051', '2013-04-01 13:09:05', '2013-04-01 13:09:05', '硬件及第三方产品采购事宜', '朱筱巍', '此项目在建设过程中，除了需要进行系统的开发与实施之外，还需要有相关硬件和第三方BI产品（BO）的支撑。经与管理系统支撑部沟通确认，之前公司集采的一套BO系统已用于预算系统，无额外的license可用；同时针对BO产品的部署，目前也无服务器可利旧，需另行采购。经估算总的采购成本大约在20 – 25万之间，具体如下：
（1）	BO产品：最低价格15万左右；
	CrystalReport，license：1User
	BO Enterprise Premium，license：5Users
	BO Web Intelligence，license：1User
	BO Xcelsius Enterpirse，license：1User
	Xcelsius Enterprise Interactive Viewing，license：5User
（2）	服务器：PC Server，型号（HP DL或IBM X系列），价格在5-10万之间。
以上内容计划提交客户审核.', 0, '2013-04-01 00:00:00', '王浩', 'visit', '8a079cef3d598664013db45ffd980048', '8a53b7823cf1edfd013cf1eface40000');
INSERT INTO  ila_action(ID, CREATED_DATE, LAST_MODIFIED_DATE, BRIEF, CUSTOMER_PARTICIPANTS, DETAIL, MILESTONE, OCCUR_DATE, SELF_PARTICIPANTS, ACTION_TYPE_ID, LEADS_ID, USER_ID) VALUES ('8a079cef3d598664013dc4f50a810052', '2013-04-01 17:37:06', '2013-04-01 17:37:06', '客户立项进度沟通', '周蕾', '目前客户立项流程已到公司副总(对框架合同有疑义)审核,相关部门在争取中,预计在4月份完成审批流程,并签订框架合同,然后将启动MIS拓展和审计系统的维护合同签订事宜.', 0, '2013-04-01 00:00:00', '王浩', 'visit', '40288c5e3cf7471f013cfaa95bd10002', '8a53b7823cf1edfd013cf1eface40000');
INSERT INTO  ila_action(ID, CREATED_DATE, LAST_MODIFIED_DATE, BRIEF, CUSTOMER_PARTICIPANTS, DETAIL, MILESTONE, OCCUR_DATE, SELF_PARTICIPANTS, ACTION_TYPE_ID, LEADS_ID, USER_ID) VALUES ('8a079cef3d598664013dc4fe89130053', '2013-04-01 17:47:28', '2013-04-01 17:47:28', '项目需求调研', '赵魁\刘来平等', '经过此次调研,对于该项目总结如下:
1、从系统定位上看,该项目将会作为银联的核心系统之一,客户非常重视
2、从商务角度看,目前存在以下竞争:客户自己和雁联
（1）雁联对此次调研工作做了充足的准备要拿下银联的这个项目；
（2）银联开发中心认为有信心自主开发。
3、优劣势分析
（1）雁联原是深圳人民银行下属的三产公司，前两年资产清理的时候，由于规模过大，已独立出来为公司，该公司正在过CMMI4，准备上市。优势：做支付相关接口比较多，做银行相关项目较多； 劣势：对财务核算，资金管理方面案例较少。
（2）银联开发中心：自主开发可节约资金，同时可以做到核心业务的保密，但他们在核算方面经验不足，还是需要我们支撑
（3）我们的优势：在核算、资金管理、二次开发和实施能力比较优秀；劣势：用户担心我们在人民银行相关接口（大小额接口、同城支付接口）没有开发经验。
以上情况，需要建立与银联的高层之间的对话机制，从而明确后续市场策略。
', 0, '2013-03-22 00:00:00', '杨兆昱', 'visit', '8a079cef3d21ac18013d244b51ed0003', '8a53b7823cf1edfd013cf1eface40000');
