INSERT INTO  ila_action(ID, CREATED_DATE, LAST_MODIFIED_DATE, BRIEF, CUSTOMER_PARTICIPANTS, DETAIL, MILESTONE, OCCUR_DATE, SELF_PARTICIPANTS, ACTION_TYPE_ID, LEADS_ID, USER_ID) VALUES ('8a079cef3de4298e013e4e78a64a0073', '2013-04-28 10:28:49', '2013-04-28 10:28:49', '财务部主管沟通', '张磊、何岗', '分别跟张磊、何岗探讨分析项目可能性：
我首先跟他们汇报，收编马秀刚、彭文舟的事项基本告一段落，我们有了人和产品，希望他们能帮我推进分析项目；

张磊是财务部信息化主管，直接领导是财务部副主任姬新宇，他表示：只要需要，他全力支持，从财务部信息化主管的角度出发， 向信息化部提出建设需求；

何岗是财务部预算主管，直接领导是财务部主任朱晓民，目前是划小核算单元财务部总牵头人；
何岗表示：1、不管从个人感情，还是工作需要，都希望能帮我们促成该项目；
                 2、前期在跟公司老总、副总以及朱主任汇报工作过程中、跟信息化部沟通需求过程中，也多次提出划小后支局划小的报表展现需求，以及一些财务业财数据分析的需求，目前还没有明确要在近期做该项目的有效信息；
                 3、由于比较了解朱主任，他感觉朱主任对分析及信息化建设这一块不是很重视，朱主任认为今年的重点是满足集团改造点的要求，让司总挂帅的项目顺利完成，满足集团的考核就可以了；其次从集团MSS域集中项目角度考虑，是否需要一起考虑，也不是很能拿得准；
                 4、接下来跟各个层面的沟通中，会持续帮我们提出分析项目（作为划小项目的延伸，尤其是支局划小的报表展现）的必要性，希望我也能找朱主任再跟进一下，看看领导是否能有进一步的对我们有利的思路；
                 5、建议我们公司前期不要做太多投入，有项目，必须是先立项再做事，否则会有风险，另外他个人感觉分析项目在核算、报账、预算等基础系统改造完成以后启动分析项目的可能性最大；', 0, '2013-04-28 00:00:00', '龚斌', 'visit', '8a079cef3de4298e013e0c704031003a', '8a53b7823cf6d775013cf6da8b750002');
