INSERT INTO  ila_action(ID, CREATED_DATE, LAST_MODIFIED_DATE, BRIEF, CUSTOMER_PARTICIPANTS, DETAIL, MILESTONE, OCCUR_DATE, SELF_PARTICIPANTS, ACTION_TYPE_ID, LEADS_ID, USER_ID) VALUES ('8a079cef3de4298e013e688b3b0c0075', '2013-05-03 11:59:15', '2013-05-03 11:59:15', '商务情况跟踪', '罗德浩', '    1、 罗现目前是天翼空间对商务合作这块进行接洽的联系人。
        2、在前期的沟通下，天翼空间已对我们递交的《企业资料准备》中“腾邦商旅”安装包进行了下载，并进行了注册。
        3、罗他们对我们提供的安装包这块进行测试后，可能还需要我们提供应用的DEMO 进行演示。
        4、罗在对关于合作的方式这块提出了些建议方案：他们可以对目前市场的需求在结合我们的公司情况，提供给相关资源，让我们对其应用进行开发。他们提供平台负责推广。
        5、应用推广后，他们需要对我们在应用平台上的交易的"账号流水"进行管理，并收取相应的费用，但他们也可对收取的费用按照其他的方式进行进行返还（简单提到了这点）
        6、需要进一步的沟通，他可以帮我们联系天翼空间的肖总、江总进行接下来的洽谈。', 0, '2013-05-03 00:00:00', '张一枫', 'visit', '8a079cef3de4298e013dfd2659a60032', '8a53b7823cf6d775013cf6d9d7a60001');
