INSERT INTO  ila_action(ID, CREATED_DATE, LAST_MODIFIED_DATE, BRIEF, CUSTOMER_PARTICIPANTS, DETAIL, MILESTONE, OCCUR_DATE, SELF_PARTICIPANTS, ACTION_TYPE_ID, LEADS_ID, USER_ID) VALUES ('8a079cef3de4298e013e6995654d0088', '2013-05-03 16:49:58', '2013-05-03 16:49:58', '项目变更事宜沟通', '董波\刘舜华', '1、沟通背景
      由于之前对BO产品功能了解不足，导致对于客户的需求无法有效满足，为此需要和客户进行及时沟通和确认。由于我方之前承诺客户可实现，因此客户方对此有较大意见，还需要做进一步协调。
2、沟通内容
     （1）会计报表：客户提供的报表数据为最终结果，数据指标属于非结构化形式，无法进行结构化的还原，导致无法有效使用BO进行满足，但如果采用其他方式势必造成较大工作量
     （2）分析报表：如果不考虑会计月报，则可以采用数据建模方式，采用BO或自主研发方式实现
3、结论
     （1）我们提出对于项目需求作适当调整，取消会计报表需求，增加分析报表需求范围，并由客户向领导汇报确认
     （2）对于分析报表需求，需要重新进行梳理，一方面由客户收集需求，另一方面结合我们的经验提出相关的解决方案，待讨论后再明确后续工作
     （3）由于目前框架合同尚未签订，再加上客户方项目负责人员近期处于岗位竞聘过程中，立项工作需要继续跟进', 0, '2013-05-02 00:00:00', '黄飞\王浩', 'visit', '8a079cef3d598664013db45ffd980048', '8a53b7823cf1edfd013cf1eface40000');
