INSERT INTO  ila_action(ID, CREATED_DATE, LAST_MODIFIED_DATE, BRIEF, CUSTOMER_PARTICIPANTS, DETAIL, MILESTONE, OCCUR_DATE, SELF_PARTICIPANTS, ACTION_TYPE_ID, LEADS_ID, USER_ID) VALUES ('8a079cef3eac7752013ef01d8c670037', '2013-05-29 19:47:48', '2013-05-29 19:47:48', '2013财辅维护采购信息沟通', '区采购部主管韦焕华', '根据昨天下午收到采购部采购邮件，沟通财辅维护采购
1、对于邮件中最终报账，是指由业务部门沟通的价格还是与采购部沟通合同价格
韦：根据采购询价四点原则来判断，没有合同约定的，可按工作量或参照以前采购单价等信息来评估，对于软件系统维保费用，结合历年各厂商维护费用，价格一般是占合同价的8%-10%，超过这个比例，基本没有。建议本次报价，不要超过10%
2、对于签订乙方签订身份有具体要求?
韦：合同主要有单位盖章就可以了
3、针对邮件中提交采购资料时点，是不是有时效管控？目前技术规范、评定办法，共享中心主管尚未有定稿，同时我们公司内部也有一个合同信息审核流程，也有可能影响到邮件所提时点延后，是否可以延后提交。
韦：对于时点，我们没有硬性规定，但希望是越快越好，收到资料快，我们就办理得快一点。另外对于技术规范、评定办法，建议可以参考TMS系统维保合同一些信息
4、商务应答方案书是指哪一文档
韦：主要是合同条款模板，公司是否有疑义，需修改地方提出来

', 0, '2013-05-29 00:00:00', '刘金钒', 'visit', '8a079cef3de4298e013df9277b75002e', '8a079cef3d598664013d7b99a8ae0020');
