INSERT INTO  ila_action(ID, CREATED_DATE, LAST_MODIFIED_DATE, BRIEF, CUSTOMER_PARTICIPANTS, DETAIL, MILESTONE, OCCUR_DATE, SELF_PARTICIPANTS, ACTION_TYPE_ID, LEADS_ID, USER_ID) VALUES ('8a079cef3fccc553013fe6540dc10034', '2013-07-16 15:13:55', '2013-07-16 15:13:55', '了解维护合同资料评价情况', '区采购部韦焕华', '通过电话现韦焕华了解上周提交的2013年财辅维保资料是否符合合同采购要求，韦回复这两天工作比较多，尚未检查合同资料，明天检查后再通知项目组。计划明天下班前了解一下检查情况', 0, '2013-07-16 00:00:00', '刘金钒', 'telephone', '8a079cef3de4298e013df9277b75002e', '8a079cef3d598664013d7b99a8ae0020');
INSERT INTO  ila_action(ID, CREATED_DATE, LAST_MODIFIED_DATE, BRIEF, CUSTOMER_PARTICIPANTS, DETAIL, MILESTONE, OCCUR_DATE, SELF_PARTICIPANTS, ACTION_TYPE_ID, LEADS_ID, USER_ID) VALUES ('8a079cef3fccc553013fe65e0e580035', '2013-07-16 15:24:51', '2013-07-16 15:24:51', '划小水电报账接口业务沟通', '共享中心：李桂香、曾繁才、韦莹、谭樱', '共享中心由于未参与水电划小需求沟通，因此通过项目组介绍了解水电报账业务
1、介绍划小水电借款，目前通过借款向供应商或者员工支付借款，往来挂账供应商类型是一次性供应商。
2、划小结账业务，通过结账业务，直接生成成本单，进行支付水电费用，未做挂账处理
3、划小销账业务，通过销账业务，冲销借款往来，但冲销条件是全额冲销。
4、划小未有相关报表反馈报账。
通过介绍，共享中心认为有3个问题
1、供应商采用一次性供应商进行挂账，影响往来挑对业务。解决方案，需接口提供按员工或正常供应商信息进行挂账，取CPMIS供应商库信息
2、全部冲销，对最先取得发票的报账，可能影响营改增专票流转时间，需提供部分冲销功能
3、业财之间数据核对，需提供相应报表查询
上述三个问题，需由共享中心重新提交新增需求文档，方可解决', 0, '2013-07-15 00:00:00', '刘金钒', 'meeting', '8a079cef3eac7752013fcc88449a00b0', '8a079cef3d598664013d7b99a8ae0020');
