INSERT INTO  ila_action(ID, CREATED_DATE, LAST_MODIFIED_DATE, BRIEF, CUSTOMER_PARTICIPANTS, DETAIL, MILESTONE, OCCUR_DATE, SELF_PARTICIPANTS, ACTION_TYPE_ID, LEADS_ID, USER_ID) VALUES ('8a079cef3fccc55301402d80bf34006c', '2013-07-30 10:55:46', '2013-07-30 10:55:46', '维保报价沟通', '区采购部韦焕华', '刘：韦老师好，打扰您几分钟，接到您的维保合同比例的反馈，我也向共享中心主管、公司反馈这信息了，对集团文件规定，我们也是尊重的。这几天忙着财务报账流程调整事情，没及时向您反馈，非常抱谦。对于系统维护情况，我向您汇报，希望区公司也在这方面结合考滤。目前2010年SSC财辅影像合同共有两个系统，一是财辅系统，全区31个单位，在用流程表单23个，共700多个报账流程，全区10000个用户，每月约1.6万个报账单，14个系统接口，二是影像系统，96台扫描仪，143多个用户，每月11.5万张，供CPMIS、财辅系统，全部共有6台机器维护，目前财辅整个项目组有三个人进行维护支撑，从2001年到2012这段时间，无论是从新逸公司还是后来新致公司，我们对广西电信现场维护工作一直坚持不懈提供最好的服务。整个维护质量及工作量IT部、共享中心是比较了解的。根据您的反馈信息采购信息，目前没有向财务部领导反馈，只是向共享中心主管汇报了一下，对于这个调整，主管担心影响到服务质量，希望我向采购部争取一下。整个项目团队成本支出，一个月差不多两万多，我们考滤是服务，按目前集团约定比例，对于我们维护成本就更大了，公司有可能出于成本因素，对人员进行调整，这样难免会影响服务质量，所以希望区公司考滤一下我们的难处。
韦：从你反馈情况来看，是希望坚持24万这个报价，对于工作量方面来说，领导不是不认可，但认可的范围也是在集团约定4%-7%之间，在SSC财辅合同没有约定比例情况下，2010年集团财务下有这个文（文件已在采购部阅读，确实存在这个条款，应用软件维保费用不高于原值的4%-7&），领导也是根据集团规定考滤。对于发起采购，这笔金额虽然不算大，对我来说，是容易的，但是发起之后领导不同意，这样就难看了，这样子，我先口头向领导汇报请示之后，再跟你说，如果领导不同意，能不能签成，哪我也没办法。
刘：非常感谢。我收到您的请示汇报结果后，再具体沟通，我们也希望事情是双赢的，如果领导坚持按集团这个比例，我们也是尊重的，我们依然希望在服务质量不受影响，但在公司坚持要调整而影响到服务质量，这方面我也会向财务部、共享中心领导反馈，希望他们能理解。', 0, '2013-07-30 00:00:00', '刘金钒', 'visit', '8a079cef3de4298e013df9277b75002e', '8a079cef3d598664013d7b99a8ae0020');
