INSERT INTO  ila_action(ID, CREATED_DATE, LAST_MODIFIED_DATE, BRIEF, CUSTOMER_PARTICIPANTS, DETAIL, MILESTONE, OCCUR_DATE, SELF_PARTICIPANTS, ACTION_TYPE_ID, LEADS_ID, USER_ID) VALUES ('8a079cef3fccc55301405bcb71af009d', '2013-08-08 10:39:54', '2013-08-08 10:39:54', '项目停目执行', '川旅平台（业务方）项目人员主持，川旅平台（承接商）项目人员', '议题：对B2C内容进行范围确定
1、系统范围：酒店管理纳入系统范围。产品管理纳入系统范围，景点管理纳入系统范围。促销管理纳入系统范围。
2、接口范围：银行接口，目前明确两个。保险接口（太平洋保险），支付宝接口，预付卡接口。
3、在此内容上，川旅平台（业务方）还是要求在9月20号所有内容全部上线，含美工。
4、人员投入要求：川旅平台（业务方）要求B2B,B2C项目至少每月投入21人。9月20日后还要继续为系统正常运营投入人保障系统。
由于上面的问题，与项目沟通过程中出入太大，表现在：
1、范围：B2C原定内容只上4家酒店，采用软件定制的方式进行，一切交易在线下。不在系统内实现。系统内只做展示。产品内容放在第二期做。景点只做图片展示。无促销与价格修改等业务功能要求。前期需求关于酒店管理，景点管理，门票管理等内容于海江都给川旅信产（承接方）提过。川旅信产（承接方）杨总(一把手）承诺无这些管理需求。
2、接口：原订只做B2B一家银行接口，且接口由川旅信产（承接方）自行开发维护。现在变为由新致负责。
3、川旅信产（承接方）承诺投入5人进入项目组。到现场发现只有2员开发人员具只有一年工作经验。不能应付局面。
4、人员投入：原定12人到现场培训，2周可回公司开发。其中5人底层开发人员会到8月底离开。现定现场纯开发人员21人，至少2个月。还需我们另提供测试人员。
5、时间节点9月20日未变。
综上结果，结合会议与现场来看，项目组核心人员（于江海，刘天军，袁柯（项目经理），郑智强，周鹏）得出如下结论：
川旅信产（承接方）的人没有需求分析能力，更谈不上需求控制能力。川旅平台（业务方）无论在技术能力掌握上、业务需求把控上、对后期运营上都有明确的思路与考虑。且对项目的验收上有明确的态度与底线。如按川旅信产（承接方）的要求，该项目完全不能上线使用。也不能拿到验收证书，对新致公司的收款严重受景响，故项目在范围、内容、时间、质量、成本、进度、变更、管理方面都存在相当大的风险。故决定项目停止执行，立即止损。上报杜总，时总批准。并已通知川旅信产（承接方）。并做扫尾工作。', 0, '2013-08-07 00:00:00', '于海江 刘天军 袁柯（项目经理)及以下。', 'visit', '8a079cef3eac7752013ec666628e001c', '8a53b7823cf6d775013cf6d9d7a60001');
