INSERT INTO  ila_action(ID, CREATED_DATE, LAST_MODIFIED_DATE, BRIEF, CUSTOMER_PARTICIPANTS, DETAIL, MILESTONE, OCCUR_DATE, SELF_PARTICIPANTS, ACTION_TYPE_ID, LEADS_ID, USER_ID) VALUES ('8a079cef3fccc55301408121b63c00ab', '2013-08-15 16:40:04', '2013-08-15 16:40:04', '首页设计需求及广东考察情况了解', '周志', '刘：根据上次沟通首页设计，完全按照需求设计，工作量比较大，如果在公告、知识库、新闻图片、报表采用四川模板，工作量会少一点，而且进度比较快，但存在缺点就在知识库方面，无法象需求所要求一样。
周：对比一下中博共享平台系统，知识库方面还是需要提供主题、内容搜索以及文档编辑功能，类似百度快照最好，如果没有这块，把系统放上去，也不会有很好使用效果，对于首页设计不要象财辅页面哪样简洁。
刘：你所说的功能，我初步和研发沟通了，都可以做，至于立项方面，是否可以开始？
周：这个我先向领导提一提财辅系统界面优化，看领导的意思再进行。
刘：上周听说你们到广东考察系统了，有什么新收获
周：去了两天，我这边主要做了一些业财方面考察，广东有广东思路，但可能在广西来说，不一定适合。另外预算系统，他们是用项目维度去管控（象陕西电信预算系统类似），这个思路早期我们也提过，但在广西这种情况，人员及管理力度还不够。目前广西是粗放型的，象本部预算各部门根本就不怎么管，不够就让财务导，其实广西目前也有种功能，通过专项活动和预算项目两个维度来管理，只是管理不到位
刘：项目维度管控思路，我们在陕西也是这种思路，可以借鉴借鉴。上次交流会，骆主任也提过。
周：这个需领导把握。', 0, '2013-08-15 00:00:00', '', 'visit', '8a079cef3eac7752013fcc88449a00b0', '8a079cef3d598664013d7b99a8ae0020');
